<?php		$lang->addModSpecificLocalization('fileadmin');	$smarty->assign('lang', $lang->getAll());	require_once('mod/default/fileadmin/fileadmin.function.php');		if ($login->currentUser() !== false && $rights->isAllowed('fileadmin', 'use')) {			$root['name'] = 'fileadmin';		$root['path'] = $dir;		$tree = makeTree($root);		$smarty->assign('tree', $tree);				$pages = $content->getPages();		$smarty->assign('pages', $pages);				if ($db->num_rows('mod', "`mod`='formmaker'") > 0) {			$forms = $db->selectList('formmaker', '*', '1', '`title`');			if (@count($forms) > 0) {				foreach ($forms as $i => $form) {					$forms[$i]['url'] = makeURL($form['key']);				}			}			$smarty->assign('forms', $forms);		}				$mods = $db->selectList('mod');		foreach ($mods as &$mod) {			$mod['url'] = makeURL($mod['mod']);		}		$smarty->assign('mods', $mods);				$smarty->assign('mode', strip_tags(@$_GET['mode']));		$smarty->assign('id', strip_tags(@$_GET['id']));		$smarty->display('../mod/default/fileadmin/browser.tpl');	}?>